import React, { useState } from "react";
import { OpenAI } from "openai";
import "./App.css";

const apiKey = "6e5d0d6cc29142e4b6540499c011dc47";  // Secure this key in env file!
const baseURL = "https://api.aimlapi.com/v1";
const systemPrompt = "You are a learning agent. You analyze different learning paths and give feedback.";

const api = new OpenAI({ apiKey, baseURL });

function App() {
  const [userInput, setUserInput] = useState(""); // Stores the user’s input
  const [aiResponse, setAiResponse] = useState(""); // Stores the AI’s response
  const [loading, setLoading] = useState(false); // Shows loading state

  // Function to call the AI API
  const fetchAIResponse = async () => {
    if (!userInput.trim()) return; // Prevent empty input

    setLoading(true);
    try {
      const completion = await api.chat.completions.create({
        model: "mistralai/Mistral-7B-Instruct-v0.2",
        messages: [
          { role: "system", content: systemPrompt },
          { role: "user", content: userInput },
        ],
        temperature: 0.7,
        max_tokens: 256,
      });

      setAiResponse(completion.choices[0].message.content);
    } catch (error) {
      console.error("API call error:", error);
      setAiResponse("Error fetching response.");
    }
    setLoading(false);
  };

  // Handles form submission (Enter key or button click)
  const handleSubmit = (e) => {
    e.preventDefault();
    fetchAIResponse();
  };

  return (
    <div className="App">
      <h1>AI Chat</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Ask something..."
        />
        <button type="submit">Send</button>
      </form>
      {loading ? <p>Loading...</p> : <p><strong>AI:</strong> {aiResponse}</p>}
    </div>
  );
}

export default App;
