import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "../components/Navbar"; // Import Navbar
import ParticlesBackground from './ParticlesBackground';
import "../App.css";

const API_KEY = "6e5d0d6cc29142e4b6540499c011dc47"; // Secure in .env
const BASE_URL = "https://api.aimlapi.com/v1/chat/completions";

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchAIResponse = async () => {
    if (!userInput.trim()) return;

    setLoading(true);
    setAiResponse("");

    try {
      const response = await fetch(BASE_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${API_KEY}`,
        },
        body: JSON.stringify({
          model: "mistralai/Mistral-7B-Instruct-v0.2",
          messages: [
            { role: "system", content: "You are a helpful learning path agent that gives feedback, tips" },
            { role: "user", content: userInput },
          ],
          temperature: 0.7,
          max_tokens: 256,
        }),
      });

      const data = await response.json();
      console.log("API Response:", data); // Debugging

      // Ensure response structure matches expected format
      if (data.choices && data.choices.length > 0) {
        setAiResponse(data.choices[0].message?.content || "No response from AI.");
      } else {
        setAiResponse("Unexpected response format.");
      }
    } catch (error) {
      console.error("API call error:", error);
      setAiResponse("Error fetching response.");
    }    

    setLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchAIResponse();
  };

  return (
    <Router>
      <ParticlesBackground />
      <Navbar /> {/* Add the Navbar */}
      <Routes>
        <Route
          path="/"
          element={
            <div className="App">
              <h1>AI Chat</h1>
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  placeholder="Ask something..."
                />
                <button type="submit">Send</button>
              </form>
              {loading ? <p>Loading...</p> : <p><strong>AI:</strong> {aiResponse}</p>}
            </div>
          }
        />
        <Route path="/signup" element={
          <div className="Home">
            <h1>Welcome To Chaintask</h1>
            <p>Welcome to Chaintask â€“ where every learning journey is uniquely yours. Our platform seamlessly integrates advanced AI with expertly curated learning paths, ensuring that no matter what you want to explore, you have your very own personal AI guide. With Chaintask, transform the way you learn into a smart, engaging, and tailored experience designed to empower your growth every step of the way.</p>
            <div className="information-containers">
              <div className="grid-container"></div>
              <div className="grid-container"></div>
              <div className="grid-container"></div>
            </div>
          </div>
        } />
        <Route path="/signin" element={<h1>Contact Page</h1>} />
      </Routes>
    </Router>
  );
}

export default App;
