import React, { useState } from 'react';
import { BookOpen, Brain, Target, Layout } from 'lucide-react';

const Navbar = () => (
  <nav className="bg-white/10 backdrop-blur-lg">
    <div className="max-w-6xl mx-auto px-4">
      <div className="flex justify-between items-center h-16">
        <div className="text-white text-xl font-bold">ChainTask</div>
        <div className="flex space-x-4">
          <button className="text-white/80 hover:text-white px-3 py-2">Features</button>
          <button className="text-white/80 hover:text-white px-3 py-2">Learning Paths</button>
          <button className="text-white/80 hover:text-white px-3 py-2">About</button>
          <button className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg">
            Get Started
          </button>
        </div>
      </div>
    </div>
  </nav>
);

const FeatureCard = ({ icon: Icon, title, description }) => (
  <div className="bg-white/10 backdrop-blur-lg rounded-xl p-6 flex flex-col items-center text-center">
    <div className="bg-white/20 p-3 rounded-full mb-4">
      <Icon className="h-6 w-6 text-white" />
    </div>
    <h3 className="text-white text-lg font-semibold mb-2">{title}</h3>
    <p className="text-white/80 text-sm">{description}</p>
  </div>
);

const App = () => {
  const [userInput, setUserInput] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const API_KEY = "6e5d0d6cc29142e4b6540499c011dc47";
  const BASE_URL = "https://api.aimlapi.com/v1/chat/completions";

  const fetchAIResponse = async () => {
    if (!userInput.trim()) return;
    setLoading(true);
    setAiResponse('');

    try {
      const response = await fetch(BASE_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${API_KEY}`,
        },
        body: JSON.stringify({
          model: "mistralai/Mistral-7B-Instruct-v0.2",
          messages: [
            { role: "system", content: "You are a helpful learning path agent that gives feedback, tips" },
            { role: "user", content: userInput },
          ],
          temperature: 0.7,
          max_tokens: 256,
        }),
      });

      const data = await response.json();
      if (data.choices && data.choices.length > 0) {
        setAiResponse(data.choices[0].message?.content || "No response from AI.");
      } else {
        setAiResponse("Unexpected response format.");
      }
    } catch (error) {
      console.error("API call error:", error);
      setAiResponse("Error fetching response.");
    }
    setLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchAIResponse();
  };

  const features = [
    {
      icon: Brain,
      title: "AI-Powered Learning",
      description: "Advanced AI technology adapts to your learning style and pace, providing personalized guidance."
    },
    {
      icon: Target,
      title: "Custom Learning Paths",
      description: "Expertly curated paths tailored to your goals and interests, ensuring effective progress."
    },
    {
      icon: Layout,
      title: "Interactive Platform",
      description: "Engage with dynamic content and receive real-time feedback to enhance your learning experience."
    },
    {
      icon: BookOpen,
      title: "Comprehensive Resources",
      description: "Access a vast library of learning materials and exercises designed for your success."
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-purple-600 to-violet-700">
      <Navbar />

      <main className="max-w-6xl mx-auto px-4 py-16">
        {/* Hero Section */}
        <div className="text-center mb-20">
          <h1 className="text-6xl font-bold text-white mb-6">
            Your Personal AI Learning Journey
          </h1>
          <p className="text-xl text-white/90 mb-8 max-w-3xl mx-auto">
            Welcome to Chaintask – where every learning journey is uniquely yours. Our platform seamlessly integrates advanced AI with expertly curated learning paths, ensuring that no matter what you want to explore, you have your very own personal AI guide.
          </p>

          {/* AI Chat Interface */}
          <div className="bg-white/10 backdrop-blur-lg rounded-lg p-6 max-w-2xl mx-auto mt-12">
            <h2 className="text-white text-xl font-semibold mb-4">Try Our AI Learning Assistant</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                placeholder="What would you like to learn today?"
                className="w-full px-4 py-3 rounded-lg bg-white/20 text-white placeholder-white/60 border border-white/20 focus:outline-none focus:border-white/40"
              />
              <button
                type="submit"
                className="w-full px-6 py-3 bg-white/20 hover:bg-white/30 text-white rounded-lg font-medium transition-colors"
              >
                Start Learning
              </button>
            </form>

            {loading ? (
              <div className="mt-4 text-white">Creating your learning path...</div>
            ) : (
              aiResponse && (
                <div className="mt-4 p-4 bg-white/10 rounded-lg text-white">
                  {aiResponse}
                </div>
              )
            )}
          </div>
        </div>

        {/* Features Section */}
        <div className="mt-20">
          <h2 className="text-3xl font-bold text-white text-center mb-12">
            Transform Your Learning Experience
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {features.map((feature, index) => (
              <FeatureCard key={index} {...feature} />
            ))}
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center mt-20">
          <h2 className="text-3xl font-bold text-white mb-6">
            Ready to Start Your Learning Journey?
          </h2>
          <button className="bg-white text-purple-600 px-8 py-4 rounded-lg font-semibold hover:bg-white/90 transition-colors">
            Get Started Now
          </button>
        </div>
      </main>
    </div>
  );
};

export default App;
