import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "../components/Navbar"; // Import Navbar
import "../App.css";

const API_KEY = "6e5d0d6cc29142e4b6540499c011dc47"; // Secure in .env
const BASE_URL = "https://api.aimlapi.com/v1/chat/completions";

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchAIResponse = async () => {
    if (!userInput.trim()) return;

    setLoading(true);
    setAiResponse("");

    try {
      const response = await fetch(BASE_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${API_KEY}`,
        },
        body: JSON.stringify({
          model: "mistralai/Mistral-7B-Instruct-v0.2",
          messages: [
            { role: "system", content: "You are a helpful learning path agent that gives feedback, tips" },
            { role: "user", content: userInput },
          ],
          temperature: 0.7,
          max_tokens: 256,
        }),
      });

      const data = await response.json();
      console.log("API Response:", data); // Debugging

      // Ensure response structure matches expected format
      if (data.choices && data.choices.length > 0) {
        setAiResponse(data.choices[0].message?.content || "No response from AI.");
      } else {
        setAiResponse("Unexpected response format.");
      }
    } catch (error) {
      console.error("API call error:", error);
      setAiResponse("Error fetching response.");
    }    

    setLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchAIResponse();
  };

  return (
    <Router>
      <Navbar /> {/* Add the Navbar */}
      <Routes>
        <Route
          path="/"
          element={
            <div className="App">
              <h1>AI Chat</h1>
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  placeholder="Ask something..."
                />
                <button type="submit">Send</button>
              </form>
              {loading ? <p>Loading...</p> : <p><strong>AI:</strong> {aiResponse}</p>}
            </div>
          }
        />
        <Route path="/about" element={<h1>About Page</h1>} />
        <Route path="/contact" element={<h1>Contact Page</h1>} />
      </Routes>
    </Router>
  );
}

export default App;
